def cargar_comidas():
    with get_session() as session:
        for comida in COMIDAS:
            session.run("""
                MERGE (c:Comida {nombre: $nombre})
                SET c.tipo = $tipo, c.imagen = $imagen
            """, nombre=comida["nombre"], tipo=comida["tipo"], imagen=comida["imagen"])
            for ing in comida["ingredientes"]:
                session.run("""
                    MERGE (i:Ingrediente {nombre: $ing})
                    WITH i
                    MATCH (c:Comida {nombre: $nombre})
                    MERGE (c)-[:TIENE]->(i)
                """, ing=ing, nombre=comida["nombre"])